import React from 'react';

function ShortAbout() {
    return (
        <div className='ShortAbout'>
            <h1 className='about-header'>Who Are We?</h1>
            <div className='about-text'>
                <p>Welcome to the official site of Scientific and Technological Research Centre. This is a very fresh start for the world’s most innovative and youth employed Research Organization. STRC starts from providing different scientific and technological faculty. STRC associates with its 3 major associates to perform in fields of science, computerized learning and environment issues. STRC’s Centre are located at Nepal, India and United States of America. The headquarters is at Nepal. STRC has majority rights of ScienceX, TechX and DevelopX including all the other STRC program.</p>
            </div>
            <div className='learn-more'>
                <button className='learn-'></button>
            </div>
      </div>
  )
}

export default ShortAbout;
