import React from 'react';

function ShortAbout() {
    return (
        <div className='ShortAbout'>
            <h1 className='about-header'>Who Are We?</h1>
            <div className='about-text'>
                <p>Welcome to STRC where Science and Technology meets Innovation through a new phase of generation. Here, this is the right platform given to you to let your creativity flow. Let’s all take this creativity to the next level and step it up a bit by helping socially and doing great deeds towards others. STRC stands for its reputation of societal work by youth for youth and for the betterment of the future! So, join hands now and let’s build a better life in the making of a better world.</p>
            </div>
            <div className='learn-more'>
                <a href='/about' className='learn-more-button'>Learn More</a>
            </div>
      </div>
  )
}

export default ShortAbout;
